mapper.select:
   SELECT REVIEW_NO, REVIEW_TITLE, REVIEW_WRITER, REVIEW_CONTENT, REVIEW_CREATED_AT, REVIEW_MODIFIED_AT, REVIEW_HITS
	 FROM REVIEWS
    WHERE REVIEW_NO = #{reviewNo}

mapper.update:
UPDATE REVIEWS
   SET REVIEW_HITS = REVIEW_HITS + 1
 WHERE REVIEW_NO = #{reviewNo}

UPDATE REVIEWS
   SET REVIEW_TITLE = #{reviewTitle}
     , REVIEW_CONTENT = #{reviewContent}
     , REVIEW_MODIFIED_AT = TO_CHAR(SYSDATE, 'YYYY-MM-DD HH24:MI:SS')
 WHERE REVIEW_NO = #{reviewNo}
